/**
 * Type definitions for generated documentation data
 * 
 * These types match the structure generated by scripts/process-docs.js
 */

/**
 * Individual documentation file
 */
export interface DocFile {
    /** Filename (e.g., "chats.md") */
    name: string;
    /** Human-readable title extracted from first heading */
    title: string;
    /** Relative path from docs root (e.g., "architecture/chats.md") */
    path: string;
    /** URL slug without .md extension (e.g., "architecture/chats") */
    slug: string;
    /** Processed HTML content for rendering */
    content: string;
    /** Original markdown content for copy functionality */
    originalMarkdown: string;
    /** Plain text content for search indexing */
    plainText: string;
    /** Word count of the document */
    wordCount: number;
}

/**
 * Documentation folder with nested structure
 */
export interface DocFolder {
    /** Folder name (e.g., "architecture") */
    name: string;
    /** Human-readable title */
    title: string;
    /** Relative path from docs root */
    path: string;
    /** Files in this folder */
    files: DocFile[];
    /** Subfolders */
    folders: DocFolder[];
}

/**
 * Root documentation structure
 */
export interface DocStructure {
    /** Top-level files in /docs */
    files: DocFile[];
    /** Top-level folders in /docs */
    folders: DocFolder[];
}

/**
 * Search index entry
 */
export interface SearchIndexEntry {
    /** Document slug (used as unique ID) */
    id: string;
    /** Document title */
    title: string;
    /** Relative path */
    path: string;
    /** URL slug */
    slug: string;
    /** Plain text content for searching */
    content: string;
    /** Breadcrumb path (e.g., "Architecture > Chats") */
    breadcrumbs: string;
}

/**
 * Complete generated documentation data
 */
export interface DocsData {
    /** ISO timestamp when docs were generated */
    generated: string;
    /** Documentation structure tree */
    structure: DocStructure;
    /** Search index entries */
    searchIndex: SearchIndexEntry[];
}
